SELECT user_name AS userName, is_disable AS isDisable, user_id AS userId, user_login_name AS userLoginName, is_admin AS isAdmin, password AS password, mobile, email, sex, province_id AS provinceId, city_id AS cityId, area_id AS areaId, address,header, organization_id AS organizationId, create_user AS createUser, create_time AS createTime, update_user AS updateUser, update_time AS updateTime FROM t_ucenter_ms_user WHERE 1 = 1 AND user_login_name = #{userLoginName} AND password = #{password}
AND group_code = #{groupCode}
AND is_disable=0

SELECT m.menu_name as menuName, m.menu_id as menuId, m.menu_url as menuUrl , m.father_menu_id as fatherMenuId FROM t_ucenter_ms_menu m WHERE m.menu_id = #{fatherMenuId}
AND m.menu_type LIKE CONCAT('%',#{menuType},'%')
ORDER BY m.father_menu_id,m.order_index

